var modalOpens=document.querySelectorAll(".buy"),modalBasket=document.querySelector(".modal-basket"),modalCloses=document.querySelectorAll(".modal-close");for(var modalOpen of modalOpens){modalOpen.addEventListener("click",function(e){e.preventDefault(),modalBasket.classList.add("modal-show");});};for(var modalClose of modalCloses){modalClose.addEventListener("click",function(e){e.preventDefault(),modalBasket.classList.remove("modal-show");});};window.addEventListener("keydown",function(e){27===e.keyCode&&modalBasket.classList.contains("modal-show")&&(e.preventDefault(),modalBasket.classList.remove("modal-show"))});
