var modalOpen=document.querySelector(".modal-open"),modalLogin=document.querySelector(".modal-login"),modalClose=modalLogin.querySelector(".modal-close"),form=modalLogin.querySelector(".login-form"),userName=modalLogin.querySelector("#user-name"),userEmail=modalLogin.querySelector("#user-email"),textarea=modalLogin.querySelector("#user-mail");modalOpen.addEventListener("click",function(e){e.preventDefault(),modalLogin.classList.add("modal-show-form"),userName.focus()}),modalClose.addEventListener("click",function(e){e.preventDefault(),modalLogin.classList.remove("modal-show-form")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalLogin.classList.contains("modal-show-form")&&(e.preventDefault(),modalLogin.classList.remove("modal-show-form"))}),form.addEventListener("submit",function(e){userName.value&&userEmail.value&&textarea.value||(e.preventDefault(),console.log("Нужно ввести логин и пароль"))});