var modalOpen=document.querySelector(".modal-open"),modalLogin=document.querySelector(".modal-login"),modalClose=document.querySelector(".modal-close");modalOpen.addEventListener("click",function(o){o.preventDefault(),modalLogin.classList.add("modal-show")}),modalClose.addEventListener("click",function(o){o.preventDefault(),modalLogin.classList.remove("modal-show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&modalLogin.classList.contains("modal-show")&&(o.preventDefault(),modalLogin.classList.remove("modal-show"))});