var mapClose,mapOpen=document.querySelector(".map-open"),mapPopup=document.querySelector(".modal-map"),mapCloses=document.querySelectorAll(".modal-close");mapOpen.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.add("modal-show")});for (var mapClose of mapCloses)mapClose.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.remove("modal-show")});window.addEventListener("keydown",function(e){27===e.keyCode&&mapPopup.classList.contains("modal-show")&&(e.preventDefault(),mapPopup.classList.remove("modal-show"))});
